    #创建每日活动
    def adminDaily(self,environment):
        # 设置admintoken
        adminToken(environment)
        self.headers["Authorization"] = getattr(TestData, "adminToken")
        if environment == "SIT":
            topUrl = "bplussit.sinosun.com:18380/mallbbcg2"
        elif environment == "UAT":
            topUrl = "bplus-uat.sinosun.com/mallbbcg2"
        elif environment == "UATBANK":
            topUrl = "bplus-uat.sinosun.com/mallbbcg2bank"
        url = "https://{}/v3/specialoffer/admin/buyEveryday/add".format(topUrl)
        data = {
            "promotionName":getattr(TestData, "promotionName"),
            "promotionTime":getattr(TestData, "promotionTime"),
            "promotionStartTime":getattr(TestData, "promotionStartTime"),
            "isShowBanner":"1",
            "titleUrl":"https://bucket-sinosun-dev1.oss-cn-hangzhou.aliyuncs.com/jegzgPi4g9/images/admin/setting/22800ae3-b559-47ad-a740-1d3242714b50.jpg",
            "bannerUrl":"https://bucket-sinosun-dev1.oss-cn-hangzhou.aliyuncs.com/jegzgPi4g9/images/admin/setting/423625b8-ab44-4ba9-af5f-f670ff9e7a8c.jpg"
        }
        print("addDaily",data)
        res = self.http.send(url=url, method="post",headers=self.headers, json=data).json()
        print("创建每日活动",res)
        # 根据活动名称查询每日活动
        url = "https://{}/v3/specialoffer/admin/buyEveryday/listPage".format(topUrl)
        data = {
            "pageSize": 10,
            "pageIndex": 1,
            "keyword": getattr(TestData, "promotionName"),
            "states": ["0"]
        }
        print("searchDaily", data)
        res = self.http.send(url=url, method="post", headers=self.headers, json=data).json()
        promotionId = res["data"]["list"][0]["promotionId"]
        setattr(TestData, "promotionId", promotionId)

        #审核活动
        url = "https://{}/v3/specialoffer/admin/buyEveryday/auditPromotion".format(topUrl)
        data = {
            "state": 1, "promotionId": getattr(TestData, "promotionId")
        }
