    # 根据关键字搜索商品，保存sku列表
    #seller添加商品并参加活动
    def addProducts(self, environment):
        # 设置admintoken
        sellerToken(environment)
        self.headers["Authorization"] = getattr(TestData, "sellerToken")
        if environment == "SIT":
            topUrl = "bplussit.sinosun.com:18380/mallbbcg2"
        elif environment == "UAT":
            topUrl = "bplus-uat.sinosun.com/mallbbcg2"
        elif environment == "UATBANK":
            topUrl = "bplus-uat.sinosun.com/mallbbcg2bank"
        url = "https://{}/v3/goods/seller/product/searchByKeyword".format(topUrl)
        i = random.randint(0, 4246)
        setattr(TestData, "keywords", words[i])
        data = {
            "keyword":getattr(TestData,"keywords"),
            "pageSize":20,"storeId":"6","pageIndex":1
        }
        res = self.http.send(url=url, method="post", headers=self.headers, json=data).json()
        old_skus = res["data"]["list"]
        # 如果根据关键字查询返回的list没有数据，则再次查询，直到list有数据
        while len(old_skus) == False:
            i = random.randint(0, 4246)
            setattr(TestData, "keywords", words[i])
            data = {
                "keyword": getattr(TestData, "keywords"),
                "pageSize": 20, "storeId": "6", "pageIndex": 1
            }
            res = self.http.send(url=url, method="post", headers=self.headers, json=data).json()
            old_skus = res["data"]["list"]
        # 关键词搜索 商品列表存到变量里
        setattr(TestData, "old_skus", old_skus)

        #过滤掉已参加活动的sku
        url = "https://{}/v3/specialoffer/seller/common/listJoinPromotionSkus".format(topUrl)
        datelist = getattr(TestData, "promotionTime")
        setattr(TestData, "dateList", datelist)
        data = {"dateList": [getattr(TestData, "dateList")]}
        print("addProducts", data)
        res = self.http.send(url=url, method="post", headers=self.headers, json=data).json()
        print("res",res)
        skus = res["data"]["skuInfos"][0]["skus"]
        # 过滤了已参加活动的商品列表
        old_skus = getattr(TestData, "old_skus")
        new_skus = []
        for li in old_skus:
            if li["sku"] not in skus:
                new_skus.append(li)
        # 存商品列表
        setattr(TestData, "new_skus", new_skus)

        #选择商品参加天天专场活动
        url = "https://{}/v3/specialoffer/seller/buyEveryday/addProducts".format(topUrl)
        products = []
        new_skus = getattr(TestData, "new_skus")
        for skus in new_skus:
            pro = {}
            pro["sku"] = skus["sku"]
            pro["promotionPrice"] = skus["salePrice"] - 1
            pro["promotionStock"] = 99
            pro["upperLimit"] = 2
            pro["markingPrice"] = skus["salePrice"] + 1
            products.append(pro)
        # print(products)
        # 组装添加的商品数据,并添加商品
        setattr(TestData, "products", products)
        data = {"promotionId":getattr(TestData, "promotionId"),
                "products":getattr(TestData, "products")
                }
        res = self.http.send(url=url, method="post", headers=self.headers, json=data).json()
        return res

