from common.config import ReadConfig

ALI_PAY_TEST = {
    "basePayType": "ALI_PAY",
    "payType": "ALI_PAY_TEST",
    "payTypeName": "支付宝测试",
    "alias": "支付宝测试",
    "icon": "data:image/png;base64,"}

ALI_PAY = {
    "basePayType": "ALI_PAY",
    "payType": "ALI_PAY",
    "payTypeName": "支付宝",
    "alias": "支付宝",
    "icon": "data:image/png;base64,"}
WX_PAY_TEST = {
    "basePayType": "WX_PAY",
    "payType": "WX_PAY_TEST",
    "payTypeName": "微信测试",
    "alias": "微信测试",
    "icon": "data:image/png;base64,"}
WX_PAY = {
    "basePayType": "WX_PAY",
    "payType": "WX_PAY",
    "payTypeName": "微信",
    "alias": "微信",
    "icon": "data:image/png;base64,"}


class BasePay:
    def __init__(self, env):
        self.env = env
        self.config_info = ReadConfig(env)
        self.production_env = self.config_info.production_env

    # 获取微信支付配置
    def get_wx_pay(self):
        if self.env.upper() == self.production_env.upper():
            return WX_PAY
        else:
            return WX_PAY_TEST

    # 获取支付宝配置
    def get_ali_pay(self):
        if self.env.upper() == self.production_env.upper():
            return ALI_PAY
        else:
            return ALI_PAY_TEST
